import { Component, Input, OnDestroy } from '@angular/core';
import { Observable, of, Subject } from 'rxjs';
import { BreakpointObserver, ViewportMode } from '@guillotinaweb/pastanaga-angular';
import { IDemoMenuSection } from './demo-menu/demo-menu.component';

// tslint:disable:max-line-length
const IMAGE =
    '';
const b64toBlob = (b64Data: string, contentType: string, sliceSize?: number) => {
    contentType = contentType || '';
    sliceSize = sliceSize || 512;

    const byteCharacters = window.atob(b64Data);
    const byteArrays: Uint8Array[] = [];

    for (let offset = 0; offset < byteCharacters.length; offset += sliceSize) {
        const slice = byteCharacters.slice(offset, offset + sliceSize);

        const byteNumbers = new Array(slice.length);
        for (let i = 0; i < slice.length; i++) {
            byteNumbers[i] = slice.charCodeAt(i);
        }

        const byteArray = new Uint8Array(byteNumbers);

        byteArrays.push(byteArray);
    }

    return new Blob(byteArrays, { type: contentType });
};
export const avatar = of(b64toBlob(IMAGE, 'image/gif'));

@Component({
    selector: 'pa-demo',
    templateUrl: './demo.component.html',
})
export class DemoComponent implements OnDestroy {
    @Input() menu: IDemoMenuSection[] = [];
    @Input() logo = '';

    mode: Observable<ViewportMode> = this.breakpoint.currentMode;
    terminator = new Subject();

    constructor(private breakpoint: BreakpointObserver) {
    }

    ngOnDestroy() {
        this.terminator.next();
        this.terminator.complete();
    }
}
