import { ChangeDetectionStrategy, Component } from '@angular/core';
import { Avatar } from 'pastanaga-angular';
import { of } from 'rxjs';

// tslint:disable:max-line-length
const IMAGE_PORTRAIT = '';
const b64toBlob = (b64Data: string, contentType: string, sliceSize?: number) => {
    contentType = contentType || '';
    sliceSize = sliceSize || 512;

    const byteCharacters = window.atob(b64Data);
    const byteArrays: Uint8Array[] = [];

    for (let offset = 0; offset < byteCharacters.length; offset += sliceSize) {
        const slice = byteCharacters.slice(offset, offset + sliceSize);

        const byteNumbers = new Array(slice.length);
        for (let i = 0; i < slice.length; i++) {
            byteNumbers[i] = slice.charCodeAt(i);
        }

        const byteArray = new Uint8Array(byteNumbers);

        byteArrays.push(byteArray);
    }

    const blob = new Blob(byteArrays, {type: contentType});
    return blob;
};

@Component({
    selector: 'pa-avatar-doc',
    templateUrl: './avatar-doc.component.html',
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class AvatarDocComponent {
    avatars: Avatar[] = [
        new Avatar({username: 'John Doe'}),
        new Avatar({username: 'Foo Bar', id: 'foo'}),
        new Avatar({username: 'ۺۼۿܐܒܯݍ ޥޱߊߪߴߵߺࠀࠕࠚ', id: 'ۺۼۿܐܒܯݍޥޱߊߪߴߵߺࠀࠕࠚ'}),
        new Avatar({username: 'With image', image: of(b64toBlob(IMAGE_PORTRAIT, 'image/gif'))}),
        new Avatar({username: 'Custom background', backgroundColor: '#FBC452'}),
        new Avatar({username: 'Custom source and background', backgroundColor: '#FBD558'}),
        new Avatar({username: 'With lock', id: 'lock', badgeIcon: 'lock'}),
        new Avatar({username: 'With lock', image: of(b64toBlob(IMAGE_PORTRAIT, 'image/gif')), badgeIcon: 'lock'}),
        new Avatar({username: 'As button'}),
    ];
    model = `avatars: Avatar[] = [
    new Avatar({username: 'John Doe'}),
    new Avatar({username: 'Foo Bar', id: 'foo'}),
    new Avatar({username: 'ۺۼۿܐܒܯݍ ޥޱߊߪߴߵߺࠀࠕࠚ', id: 'ۺۼۿܐܒܯݍޥޱߊߪߴߵߺࠀࠕࠚ'}),
    new Avatar({username: 'With image', image: of(b64toBlob(IMAGE_PORTRAIT, 'image/gif'))}),
    new Avatar({username: 'Custom background', backgroundColor: '#FBC452'}),
    new Avatar({username: 'Custom source and background', backgroundColor: '#FBD558'}),
    new Avatar({username: 'With lock', id: 'lock', badgeIcon: 'lock'}),
    new Avatar({username: 'With lock', image: of(b64toBlob(IMAGE_PORTRAIT, 'image/gif')), badgeIcon: 'lock'}),
    new Avatar({username: 'As button'}),
];`;
    code = `<pa-avatar *ngFor="let avatar of avatars; let i = index"
           [size]="i < 6 ? 'small' : 'regular'"
           [isButton]="i === avatars.length - 1"
           [avatar]="avatar"
           [src]="i === 5 ? './assets/ninja.svg' : null"></pa-avatar>`;
}
